import React, { useState, useRef, useEffect } from "react";
import { TreeSelect, Input, Select, Button } from "antd";
import { FolderFilled, DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";
import './styles.css'
const { TreeNode } = TreeSelect;
const { Option } = Select;
const { Search } = Input;


const Model = () => {
  const focusDiv = useRef();
  const [open, setOpen] = useState(true);
  const onChange = (value) => {
    // setValue(value);
  };


  const treeData = [
    {
      title: "asda",
      key: "0-0",
      children: [
        {
          title: "leaf 0-0",
          key: "0-0-0",
          isLeaf: true
        },
        {
          title: "leaf 0-1",
          key: "0-0-1",
          isLeaf: true
        }
      ]
    },
    {
      title: "parent 1",
      key: "0-1",
      children: [
        {
          title: "leaf 1-0",
          key: "0-1-0",
          isLeaf: true
        },
        {
          title: "leaf 1-1",
          key: "0-1-1",
          isLeaf: true
        }
      ]
    }
  ];

  return (
    <div style={{ width: "400px", margin: '30px auto' }}>
      <TreeSelect
        treeIcon
        treeDefaultExpandAll
        className='g-tree-select'
        dropdownRender={(prop) => {
          return <>
          <div className="g-search-header">
            <Search
              placeholder="Search"
              style={{ width: '254px' }}
              onChange={onChange}
            />
            {/* <span
              style={{ color: 'blue', cursor: 'pointer' }}
              onClick={() => {
                console.log('handleAddFolder')
              }}
            >
              Add New Folder
            </span> */}
            <Button>Add New Folder</Button>
            </div>
            {prop}
          </>
        }}
        style={{ marginBottom: 8, width: '100%' }}
        dropdownStyle={{ height: 400, overflow: 'auto' }}
        placeholder="Please select"
        open={open}
      // onFocus={() => setOpen(!open)}
      >
        {/* <TreeNode
          key={0}
          // icon={<FolderFilled />}
          isLeaf
          treeIcon={false}
          selectable={false}
          title={
            <>
              <Search
                placeholder="Search"
                style={{ width: '226px' }}
                onChange={onChange}
              />
              <span
                style={{ color: 'blue', cursor: 'pointer' }}
                onClick={() => {
                  console.log('handleAddFolder')
                }}
              >
                Add New Folder
              </span>
            </>
          }
        /> */}
        <TreeNode
          key={1}
          icon={<FolderFilled />}
          selectable={false}
          title={
            <>
              <Input
                style={{ width: '202px' }}
                onClick={() => {
                  focusDiv.current.focus()
                }}
                ref={focusDiv} placeholder="Enter new folder name" />
            </>
          }
        >
          <TreeNode
            key={2}
            // icon={<FolderFilled />}
            selectable={false}
            title={
              <Select
                defaultValue="lucy"
                style={{ width: '202px' }}
              >
                <Option value="jack">Visible to Everyone</Option>
                <Option value="lucy">Visible to specific users</Option>
              </Select>
            }
          />
          <TreeNode
            key={3}
            // icon={<FolderFilled />}
            selectable={false}
            title={
              <Select
                defaultValue="lucy"
                style={{ width: '202px' }}
              >
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
              </Select>
            }
          />
          <TreeNode
            key={4}
            // icon={<FolderFilled />}
            selectable={false}
            title={
              <div
                style={{
                  width: '202px',
                  display: 'flex',
                  flexDirection: 'row-reverse',
                  gap: '20px'
                }}
              >
                <SaveTwoTone
                  twoToneColor="#44AEA0"
                  style={{ fontSize: '25px' }}
                />
                <DeleteTwoTone
                  twoToneColor="#D3455C"
                  style={{ fontSize: '25px' }} />

              </div>
            }
          />
        </TreeNode>
      </TreeSelect>
    </div>
  );
};

export default Model;
