import React, { useState, useRef, useEffect } from "react";
import { TreeSelect, Input } from "antd";
import { FolderFilled } from "@ant-design/icons";
import './styles.css'
const { TreeNode } = TreeSelect;
const { Search } = Input;


const Model = () => {
  const focusDiv = useRef();
  const [value, setValue] = useState();
  const onChange = (value) => {
    setValue(value);
  };

  useEffect(() => {
    if(focusDiv.current) focusDiv.current.focus(); 
   }, [focusDiv]);

  const treeData = [
    {
      title: "asda",
      key: "0-0",
      children: [
        {
          title: "leaf 0-0",
          key: "0-0-0",
          isLeaf: true
        },
        {
          title: "leaf 0-1",
          key: "0-0-1",
          isLeaf: true
        }
      ]
    },
    {
      title: "parent 1",
      key: "0-1",
      children: [
        {
          title: "leaf 1-0",
          key: "0-1-0",
          isLeaf: true
        },
        {
          title: "leaf 1-1",
          key: "0-1-1",
          isLeaf: true
        }
      ]
    }
  ];

  return (
    <div style={{ width: "400px", margin: '30px auto' }}>
      <Search
        placeholder="Search"
        onChange={onChange}
      />
      <TreeSelect
      className='g-tree-select'
      showSearch
      onSelect={(e) => {
        return
      }}
      style={{ marginBottom: 8, width: '100%' }}
      dropdownStyle={{ maxHeight: 400, overflow: 'auto' }}
      placeholder="Please select"
      >
        <TreeNode
          key={1}
          treeIcon
          icon={<FolderFilled />}
          onSelect={() => {
            console.log('----')
          }}
          selectable={false}
          title={
            <Input ref={focusDiv} placeholder="Enter new folder name" />
          }
        >
          <TreeNode
            key={2}
            icon={<FolderFilled />}
            title={
              <div>
                <span>s</span>
                <span>Another Column</span>
                <button>Click here</button>
              </div>
            }
          />
        </TreeNode>
      </TreeSelect>
    </div>
  );
};

export default Model;
