import React, { useState } from "react";
import { Input, Select } from 'antd';
import { DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";

const { Search } = Input;
const { Option } = Select;

const Action = ({ handleSave, handleDel }) => {
    return <div className="g-action"
        style={{ width: "202px" }}
    >
        <SaveTwoTone
            twoToneColor="#44AEA0"
            style={{ fontSize: "25px" }}
            onClick={(e) => {
                if (typeof handleSave === 'function') {
                    handleSave(e)
                }
            }}
        />
        <DeleteTwoTone
            twoToneColor="#D3455C"
            style={{ fontSize: "25px" }}
            onClick={(e) => {
                if (typeof handleDel === 'function') {
                    handleDel(e)
                }
            }}
        />
    </div>
}

const InputAction = ({ type, allowers, handleSave, handleDel, ...props }) => {
    const [typeSelect, setTypeSelect] = useState(type)
    console.log(type)
    return <div
        style={{
            margin: '15px 0 0 15px',
            width: '208px',
        }}
        {...props}
    >
        <Input style={{ width: "202px" }} placeholder="Enter new folder name" />
        <Select
            defaultValue={type}
            showSearch
            style={{ width: "202px" }}
            onChange={(sc) => {
                setTypeSelect(sc)
            }}
        >
            <Option value="all">Visible to Everyone</Option>
            <Option value="one">
                Visible to specific users
            </Option>
        </Select>
        {typeSelect === 'one' && <Select
            defaultValue={allowers}
            placeholder="Select a name"
            mode="multiple"
            style={{ width: "202px" }}
        >
            <Option value="jack">Jack</Option>
            <Option value="lucy">Lucy</Option>
        </Select>}
        <Action handleSave={handleSave} handleDel={handleDel} />
    </div>
}

export default InputAction
