import { Tree, Input, Select, Button } from 'antd';
import React, { useState } from 'react'
const { Search } = Input;
const { Option } = Select;

const x = 3;
const y = 2;
const z = 1;
const gData = [];

const generateData = (_level, _preKey, _tns) => {
    const preKey = _preKey || '0';
    const tns = _tns || gData;

    const children = [];
    for (let i = 0; i < x; i++) {
        const key = `${preKey}-${i}`;
        tns.push({ title: key, key });
        if (i < y) {
            children.push(key);
        }
    }
    if (_level < 0) {
        return tns;
    }
    const level = _level - 1;
    children.forEach((key, index) => {
        tns[index].children = [];
        return generateData(level, key, tns[index].children);
    });
};
generateData(z);

const dataList = [];
const generateList = data => {
    for (let i = 0; i < data.length; i++) {
        const node = data[i];
        const { key } = node;
        dataList.push({ key, title: key });
        if (node.children) {
            generateList(node.children);
        }
    }
};
generateList(gData);

const getParentKey = (key, tree) => {
    let parentKey;
    for (let i = 0; i < tree.length; i++) {
        const node = tree[i];
        if (node.children) {
            if (node.children.some(item => item.key === key)) {
                parentKey = node.key;
            } else if (getParentKey(key, node.children)) {
                parentKey = getParentKey(key, node.children);
            }
        }
    }
    return parentKey;
};

const SearchTree = () => {
    const [expandedKeys, setExpandedKeys] = useState([])
    const [searchValue, setSearchValue] = useState('')
    const [autoExpandParent, setAutoExpandParent] = useState(true)

    const onExpand = expandedkeys => {
        setExpandedKeys(expandedkeys)
        setAutoExpandParent(false)
    }

    const onChange = e => {
        const { value } = e.target;
        const expandedkeys = dataList
            .map(item => {
                if (item.title.indexOf(value) > -1) {
                    return getParentKey(item.key, gData);
                }
                return null;
            })
            .filter((item, i, self) => item && self.indexOf(item) === i);
        setExpandedKeys(expandedkeys)
        setSearchValue(value)
        setAutoExpandParent(true)
    };

    const loop = data =>
        data.map((item, index) => {
            const title =                   
                item.title === 'input' ? <>
                  <Input
                    style={{ width: '202px' }}
                    // onClick={() => {
                    //   if(elRefs.current[index].current) {
                    //     elRefs.current[index].current.focus()
                    //   }
                    // }}
                    // ref={elRefs.current[index]}
                    placeholder="Enter new folder name" />
                </> : <>
                    <span style={{ paddingLeft: '10px'}}>{item.default}</span>
                    <br></br>
                    <span style={{ paddingLeft: '35px', fontSize: '11px' }}>{item.description}</span>
                </>

            // if (item.children) {
            //     return { title, key: item.key, children: loop(item.children) };
            // }

            return {
                title,
                key: index,
            };
        });

        const treeData = [
            {
              title: "label",
              description: 'Visible to Everyone',
              key: "0",
              default: 'My folder name 1',
              children: [
                {
                  title: "select",
                  default: 'all',
                  key: "0-0",
                  isLeaf: true
                },
                {
                  title: "selectall",
                  default: ['lucy'],
                  key: "0-1",
                  isLeaf: true
                },
                {
                  title: 'action',
                  key: "0-2",
                  isLeaf: true
                }
              ]
            },
          ];

    const [selectValue, setSelectValue] = useState(undefined)
    return (
        <div style={{ width: "400px", margin: '30px auto' }}>
            {/* <Search style={{ marginBottom: 8 }} placeholder="Search" onChange={onChange} />
        <Tree
          onExpand={onExpand}
          draggable
          expandedKeys={expandedKeys}
          autoExpandParent={autoExpandParent}
          treeData={loop(gData)}
        /> */}
            <Select
                labelInValue
                // defaultValue={{ value: 'lucy' }}
                style={{ width: 400 }}
                value={selectValue}
                dropdownRender={() => {
                    return <div
                    >
                        {/* <Search style={{ marginBottom: 8 }} placeholder="Search" onChange={onChange} /> */}
                        <div className="g-search-header">
              <Search
                placeholder="Search"
                style={{ width: '254px' }}
                onChange={(ev) => {
                //   const textSearch = ev.target.value
                //   setSearch(textSearch) 
                }}
              />
              <Button
                onClick={() => {
                //   const newData = [newDataDefault,...data]
                //   setData(newData)
                }}
              >
                Add New Folder
              </Button>
            </div>
                        <Tree
                            draggable
                            height={400}
                            defaultExpandAll
                            treeData={loop(treeData)}
                        />
                    </div>
                }}
                onChange={(ev) => {
                    console.log(ev)
                }}
            >
                <Option value="lucy">Lucy (101)</Option>
            </Select>,
        </div>
    );
}

export default SearchTree
