import React, { useState } from "react";
import { Input, Select, Button } from 'antd';
import { DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";

const { Search } = Input;
const { Option } = Select;

const Action = ({ handleSave, handleDel }) => {
    return <div className="g-action"
        style={{ width: "202px" }}
    >
        <SaveTwoTone
            twoToneColor="#44AEA0"
            style={{ fontSize: "25px" }}
            onClick={() => {
                if (typeof handleSave === 'function') {
                    handleSave()
                }
            }}
        />
        <DeleteTwoTone
            twoToneColor="#D3455C"
            style={{ fontSize: "25px" }}
            onClick={() => {
                if (typeof handleDel === 'function') {
                    handleDel()
                }
            }}
        />
    </div>
}

const InputAction = (item) => {
    const [type, setType] = useState(item?.type)
    console.log(type)
    return <div
        style={{
            margin: '15px 0 0 15px',
            width: '208px',
        }}
    >
        <Input style={{ width: "202px" }} placeholder="Enter new folder name" />
        <Select
            defaultValue={item?.type}
            showSearch
            style={{ width: "202px" }}
            onChange={(sc) => {
                setType(sc)
            }}
        >
            <Option value="all">Visible to Everyone</Option>
            <Option value="one">
                Visible to specific users
            </Option>
        </Select>
        <Select
            defaultValue={item?.allowers}
            placeholder="Select a name"
            mode="multiple"
            style={{ width: "202px" }}
        >
            <Option value="jack">Jack</Option>
            <Option value="lucy">Lucy</Option>
        </Select>
        <Action />
    </div>
}

export default InputAction
