import React, { useCallback, useEffect, useState } from 'react'
import { Tree, Input, Select, Button } from 'antd';
import { FolderFilled, DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";
import InputAction from './component/InputAction'
const { Search } = Input;
const { Option } = Select;

const x = 3;
const y = 2;
const z = 1;
const oData = [];

const generateData = (_level, _preKey, _tns) => {
    const preKey = _preKey || '0';
    const tns = _tns || oData;

    const children = [];
    for (let i = 0; i < x; i++) {
        const key = `${preKey}-${i}`;
        tns.push({ title: key, key });
        if (i < y) {
            children.push(key);
        }
    }
    if (_level < 0) {
        return tns;
    }
    const level = _level - 1;
    children.forEach((key, index) => {
        tns[index].children = [];
        return generateData(level, key, tns[index].children);
    });
};

generateData(z);

const dataList = [];
const generateList = data => {
    for (let i = 0; i < data.length; i++) {
        const node = data[i];
        const { key } = node;
        dataList.push({ key, title: key });
        if (node.children) {
            generateList(node.children);
        }
    }
};
generateList(oData);

const Folder = ({ name, type, handleSelect }) => {
    return <div
        className="g-folder"
        onClick={() => {
            if (typeof handleSelect === 'function') {
                handleSelect()
            }
        }}
    >
        <div>
            <span style={{ paddingLeft: "10px" }}>
                {name}
            </span>
        </div>
        <div>
            <span style={{
                paddingLeft: "10px", fontSize: "11px"
            }}>
                {type === 'all' ? 'Visible to Everyone' : 'Visible to specific users'}
            </span>

        </div>
    </div>
}

const Action = ({ handleSave, handleDel }) => {
    return <div className="g-action"
        style={{ width: "202px" }}
    >
        <SaveTwoTone
            twoToneColor="#44AEA0"
            style={{ fontSize: "25px" }}
            onClick={() => {
                if (typeof handleSave === 'function') {
                    handleSave()
                }
            }}
        />
        <DeleteTwoTone
            twoToneColor="#D3455C"
            style={{ fontSize: "25px" }}
            onClick={() => {
                if (typeof handleDel === 'function') {
                    handleDel()
                }
            }}
        />
    </div>
}



const treeData = [
    {
        title: "label",
        name: 'My folder name 1',
        allowers: ['lucy'],
        type: 'one',
        children: [
            {
                title: "label",
                name: 'My folder name 1-1',
                allowers: [],
                type: 'all',
                children: [
                    {
                        title: "label",
                        name: 'My folder name 1-1-1',
                        allowers: [],
                        type: 'all',
                    },
                ]
            },
            {
                title: "label",
                name: 'My folder name 1-2',
                allowers: [],
                type: 'all',
            },
        ],
    },
    {
        title: "label",
        name: 'My folder name 2',
        allowers: ['lucy'],
        type: 'one',
    },
    {
        title: "input",
        name: 'My folder name 2',
        allowers: ['lucy'],
        type: 'one',
    },
];

const SearchTree = () => {
    const [searchValue, setSearchValue] = useState('')
    const [open, setOpen] = useState(false)
    const [gData, setGData] = useState([])
    const [value, setValue] = useState(undefined)
    const [save, setSave] = useState(false)

    useEffect(() => {
        const newestData = makeup(treeData)
        setGData(newestData)
    }, [])

    const handleSelectTree = ((info) => {
        setOpen(false)
        setSave(false)
        setValue({
            value: info.name,
            label: info.name,
            type: info.type,
            allowers: info.allowers
        })
    })

    const makeup = (d, level = '0') => {
        const result = d.map((node, parentIdx) => {
            const { type, allowers, name, children, title } = node
            let newTitle = title
            let newChildren = children
            if (title === 'input') {
                newTitle = <InputAction type={type} allowers={allowers} />
            }
            if (title === 'label') {
                newTitle = <Folder name={name} type={type} handleSelect={() => handleSelectTree({
                    name,
                    allowers,
                    type
                })} />
            }
            if (children) {
                newChildren = makeup(children, `${level}-${parentIdx}`)
            }
            return {
                title: newTitle,
                key: `${level}-${parentIdx}`,
                name,
                allowers,
                type,
                icon: <FolderFilled style={{ fontSize: "20px", paddingTop: "10px" }} />,
                ...children ? {
                    children: newChildren,
                } : {}
            }
        })
        return result
    }

    const onDragEnter = info => {
    };

    const onDrop = info => {
        setValue(undefined)
        setSave(false)
        const dropKey = info.node.key;
        const dragKey = info.dragNode.key;
        const dropPos = info.node.pos.split('-');
        const dropPosition = info.dropPosition - Number(dropPos[dropPos.length - 1]);

        const loop = (data, key, callback) => {
            for (let i = 0; i < data.length; i++) {
                if (data[i].key === key) {
                    return callback(data[i], i, data);
                }
                if (data[i].children) {
                    loop(data[i].children, key, callback);
                }
            }
        };
        const data = [...gData];

        // Find dragObject
        let dragObj;
        loop(data, dragKey, (item, index, arr) => {
            arr.splice(index, 1);
            dragObj = item;
        });

        if (!info.dropToGap) {
            // Drop on the content
            loop(data, dropKey, item => {
                item.children = item.children || [];
                // where to insert 示例添加到头部，可以是随意位置
                item.children.unshift(dragObj);
            });
        } else if (
            (info.node.props.children || []).length > 0 && // Has children
            info.node.props.expanded && // Is expanded
            dropPosition === 1 // On the bottom gap
        ) {
            loop(data, dropKey, item => {
                item.children = item.children || [];
                // where to insert 示例添加到头部，可以是随意位置
                item.children.unshift(dragObj);
                // in previous version, we use item.children.push(dragObj) to insert the
                // item to the tail of the children
            });
        } else {
            let ar;
            let i;
            loop(data, dropKey, (item, index, arr) => {
                ar = arr;
                i = index;
            });
            if (dropPosition === -1) {
                ar.splice(i, 0, dragObj);
            } else {
                ar.splice(i + 1, 0, dragObj);
            }
        }
        setGData(makeup(data))
    };


    const [data, setData] = useState(treeData)

    const newDataDefault = {
        title: "input",
        description: '',
        name: '',
        allowers: [],
        type: '',
    }




    // console.log(makeup(gData))
    console.log(value)
    return (
        <>
        <div style={{ width: "460px", margin: '30px auto', border: '1px solid #ded7d7', padding: '5px' }}>
            <Select
                labelInValue
                // defaultValue={{ value: 'lucy' }}
                open={open}
                style={{ width: 448 }}
                onClick={(e) => {
                    const { className } = e.target
                    const targetClass = ['ant-select-selection-search-input']
                    if (targetClass.includes(className)) {
                        setOpen(!open)
                    }
                }}
                dropdownStyle={{
                    overflow: 'auto',
                    height: '460px',
                }}
                value={value}
                placeholder="Please select"
