import React, { useState, useRef, useEffect } from "react";
import { TreeSelect, Input, Select } from "antd";
import { FolderFilled } from "@ant-design/icons";
import './styles.css'
const { TreeNode } = TreeSelect;
const { Option } = Select;
const { Search } = Input;


const Model = () => {
  const focusDiv = useRef();
  const [value, setValue] = useState();
  const onChange = (value) => {
    setValue(value);
  };


  const treeData = [
    {
      title: "asda",
      key: "0-0",
      children: [
        {
          title: "leaf 0-0",
          key: "0-0-0",
          isLeaf: true
        },
        {
          title: "leaf 0-1",
          key: "0-0-1",
          isLeaf: true
        }
      ]
    },
    {
      title: "parent 1",
      key: "0-1",
      children: [
        {
          title: "leaf 1-0",
          key: "0-1-0",
          isLeaf: true
        },
        {
          title: "leaf 1-1",
          key: "0-1-1",
          isLeaf: true
        }
      ]
    }
  ];

  return (
    <div style={{ width: "400px", margin: '30px auto' }}>
      <Search
        placeholder="Search"
        onChange={onChange}
      />
      <TreeSelect
        treeIcon
        treeDefaultExpandAll
        className='g-tree-select'
        style={{ marginBottom: 8, width: '100%' }}
        dropdownStyle={{ height: 400, overflow: 'auto' }}
        placeholder="Please select"
      >
        <TreeNode
          key={1}
          icon={<FolderFilled />}
          selectable={false}
          title={
            <>
              <Input 
              onClick={() => {
                focusDiv.current.focus()
              }}
              ref={focusDiv} placeholder="Enter new folder name" />
            </>
          }
        >
          <TreeNode
            key={2}
            // icon={<FolderFilled />}
            selectable={false}
            title={
              <Select defaultValue="lucy" style={{ width: '200px' }}>
              <Option value="jack">Jack</Option>
              <Option value="lucy">Lucy</Option>
            </Select>
            }
          />
        </TreeNode>
      </TreeSelect>
    </div>
  );
};

export default Model;
