import React, { useState, useRef, useEffect, createRef } from "react";
import { TreeSelect, Input, Select, Button } from "antd";
import { FolderFilled, DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";
import './styles.css'
const { TreeNode } = TreeSelect;
const { Option } = Select;
const { Search } = Input;


const Model = () => {
  const focusDiv = useRef();
  const treeData = [
    {
      title: "label",
      description: 'Visible to Everyone',
      key: "0",
      default: 'My folder name 1',
      children: [
        {
          title: "select",
          default: 'all',
          key: "0-0",
          isLeaf: true
        },
        {
          title: "selectall",
          default: ['lucy'],
          key: "0-1",
          isLeaf: true
        },
        {
          title: 'action',
          key: "0-2",
          isLeaf: true
        }
      ]
    },
  ];
  const newDataDefault = {
    title: "input",
    description: 'Visible to Everyone',
    default: '',
    children: [
      {
        title: "select",
        default: 'all',
        isLeaf: true
      },
      {
        title: "selectall",
        default: ['lucy'],
        isLeaf: true
      },
      {
        title: 'action',
        isLeaf: true
      }
    ]
  }

  const [open, setOpen] = useState(false);
  const [value, setValue] = useState(undefined);
  const [data, setData] = useState(treeData)
  const arrLength = data.length
  const elRefs = useRef([]);

  useEffect(() => {
    if (elRefs.current.length !== arrLength) {
      // add or remove refs
      elRefs.current = Array(arrLength).fill().map((_, i) => elRefs.current[i] || createRef());
    }
  }, [data])


  const onChange = (value) => {
    // setValue(value);
  };

  return (
    <div style={{ width: "400px", margin: '30px auto' }}>
      <TreeSelect
        treeIcon
        treeDefaultExpandAll
        className='g-tree-select'
        value={value}
        dropdownRender={(prop) => {
          return <>
            <div className="g-search-header">
              <Search
                placeholder="Search"
                style={{ width: '254px' }}
                onChange={onChange}
              />
              <Button
                onClick={() => {
                  console.log('handleAddFolder')
                  const newData = [newDataDefault,...data]
                  setData(newData)
                }}
              >
                Add New Folder
              </Button>
            </div>
            {prop}
          </>
        }}
        style={{ marginBottom: 8, width: '100%' }}
        dropdownStyle={{ maxHeight: 400, overflow: 'auto' }}
        placeholder="Please select"
        open={open}
        onChange={(vl) => {
          setValue(vl)
          setOpen(false)
        }}
        onFocus={() => {
          setOpen(true)
        }}
        onBlur={() => {
          console.log('+++++')
        }}
      // onFocus={() => setOpen(!open)}
      >
        {
          data.map((item, index) => {
            console.log(index, '++++')
            return <TreeNode
              key={index}
              value={index}
              icon={<FolderFilled style={{ fontSize: '20px', paddingTop: '10px' }} />}
              isLeaf={!!item.default}
              selectable={!!item.default}
              title={
                <>
                  {
                    !item.default ? <>
                      <Input
                        style={{ width: '202px' }}
                        onClick={() => {
                          if(elRefs.current[index].current) {
                            elRefs.current[index].current.focus()
                          }
                        }}
                        ref={elRefs.current[index]} placeholder="Enter new folder name" />
                    </> : <>
                        <span style={{ paddingLeft: '10px'}}>{item.default}</span>
                        <br></br>
                        <span style={{ paddingLeft: '35px', fontSize: '11px' }}>{item.description}</span>
                    </>
                  }
                </>
              }
            >
              {!item.default &&
                item.children.map((chil, cid) => {
                  return <TreeNode
                    key={`${index}-${cid}`}
                    selectable={false}
                    value={`${index}-${cid}`}
                    title={
                      <>
                        {
                          chil.title === 'select' ? <>
                            <Select
                              defaultValue={chil.default}
                              style={{ width: '202px' }}
                            >
                              <Option value="all">Visible to Everyone</Option>
                              <Option value="one">Visible to specific users</Option>
                            </Select>
                          </> : chil.title === 'selectall' ? <>
                            <Select
                              defaultValue={chil.default}
                              mode="multiple"
                              style={{ width: '202px' }}
                            >
                              <Option value="jack">Jack</Option>
                              <Option value="lucy">Lucy</Option>
                            </Select>
                          </> : chil.title === 'action' ? <>
                            <div
                              className="g-action"
                              style={{
                                width: '202px',
                              }}
                            >
                              <SaveTwoTone
                                twoToneColor="#44AEA0"
                                style={{ fontSize: '25px' }}
                                onClick={() => {
                                  console.log('handleSave')
                                }}
                              />
                              <DeleteTwoTone
                                twoToneColor="#D3455C"
                                style={{ fontSize: '25px' }}
                                onClick={() => {
                                  console.log('handleDelete')
                                }}
                              />

                            </div>
                          </> : ''
                        }
                      </>
                    }
                  />
                })
              }
            </TreeNode>
          })
        }
      </TreeSelect>
    </div>
  );
};

export default Model;
