import React, { useState } from "react";
import { Input, Select, Form, Button } from 'antd';
import { DeleteTwoTone, SaveTwoTone } from "@ant-design/icons";

const { Search } = Input;
const { Option } = Select;

const Action = ({ handleSave, handleDel }) => {
    return <div className="g-action"
        style={{ width: "202px" }}
    >
        <button style={{
            all: 'unset'
        }}
        type="submit"
        onClick={(e) => {
            console.log(e)
            if (typeof handleSave === 'function') {
                handleSave(e)
            }
        }}
        >

        <SaveTwoTone
            twoToneColor="#44AEA0"
            style={{ fontSize: "25px" }}
            // onClick={(e) => {
            //     console.log(e)
            //     if (typeof handleSave === 'function') {
            //         handleSave(e)
            //     }
            // }}
        />
        </button>
        <DeleteTwoTone
            twoToneColor="#D3455C"
            style={{ fontSize: "25px" }}
            onClick={(e) => {
                if (typeof handleDel === 'function') {
                    handleDel(e)
                }
            }}
        />
    </div>
}

const InputAction = ({ type, allowers, handleSave, handleDel, keyIndex }) => {
    const [typeSelect, setTypeSelect] = useState(type)
    const [form] = Form.useForm();
    console.log(keyIndex, type)
    return <div
        style={{
            margin: '15px 0 0 15px',
            width: '208px',
        }}
    >
        <Form
        form={form}
            onFinish={(e) => {
               console.log(e, '----')
           }}
        >
            <Form.Item
             name="name"
            >
                <Input style={{ width: "202px" }} placeholder="Enter new folder name" />
            </Form.Item>
            <Form.Item
                name="type"
            >
                <Select
                    // defaultValue={type}
                    showSearch
                    style={{ width: "202px" }}
                    onChange={(sc) => {
                        setTypeSelect(sc)
                    }}
                >
                    <Option value="all">Visible to Everyone</Option>
                    <Option value="one">
                        Visible to specific users
                    </Option>
                </Select>
            </Form.Item>

            {typeSelect === 'one' && <Form.Item
                name="allowers"
            >
                <Select
                    // defaultValue={allowers}
                    placeholder="Select a name"
                    mode="multiple"
                    style={{ width: "202px" }}
                >
                    <Option value="jack">Jack</Option>
                    <Option value="lucy">Lucy</Option>
                </Select>
            </Form.Item>}
            <Form.Item>
            {/* <Action handleSave={handleSave} handleDel={handleDel} /> */}
                    <Button htmlType="button">
                        sd
                    </Button>
            </Form.Item>
        </Form>
    </div>
}

export default InputAction
