import React, { useCallback, useEffect, useState } from 'react'
import { Tree, Input, Select, Button } from 'antd';
import { FolderFilled } from "@ant-design/icons";
import InputAction from './component/InputAction'
const { Search } = Input;
const { Option } = Select;

const x = 3;
const y = 2;
const z = 1;
const oData = [];

const generateData = (_level, _preKey, _tns) => {
    const preKey = _preKey || '0';
    const tns = _tns || oData;

    const children = [];
    for (let i = 0; i < x; i++) {
        const key = `${preKey}-${i}`;
        tns.push({ title: key, key });
        if (i < y) {
            children.push(key);
        }
    }
    if (_level < 0) {
        return tns;
    }
    const level = _level - 1;
    children.forEach((key, index) => {
        tns[index].children = [];
        return generateData(level, key, tns[index].children);
    });
};

generateData(z);

const dataList = [];
const generateList = data => {
    for (let i = 0; i < data.length; i++) {
        const node = data[i];
        const { key } = node;
        dataList.push({ key, title: key });
        if (node.children) {
            generateList(node.children);
        }
    }
};
generateList(oData);

const Folder = ({ name, type, handleSelect }) => {
    return <div
        className="g-folder"
        onClick={() => {
            if (typeof handleSelect === 'function') {
                handleSelect()
            }
        }}
    >
        <div>
            <span style={{ paddingLeft: "10px" }}>
                {name}
            </span>
        </div>
        <div>
            <span style={{
                paddingLeft: "10px", fontSize: "11px"
            }}>
                {type === 'all' ? 'Visible to Everyone' : 'Visible to specific users'}
            </span>

        </div>
    </div>
}

const treeData = [
    {
        title: "label",
        name: 'My folder name 1',
        allowers: ['lucy'],
        type: 'one',
        // children: [
        //     {
        //         title: "label",
        //         name: 'My folder name 1-1',
        //         allowers: [],
        //         type: 'all',
        //         children: [
        //             {
        //                 title: "label",
        //                 name: 'My folder name 1-1-1',
        //                 allowers: [],
        //                 type: 'all',
        //             },
        //         ]
        //     },
        //     {
        //         title: "label",
        //         name: 'My folder name 1-2',
        //         allowers: [],
        //         type: 'all',
        //     },
        // ],
    },
    // {
    //     title: "label",
    //     name: 'My folder name 2',
    //     allowers: ['lucy'],
    //     type: 'one',
    // },
];

const SearchTree = () => {
    const [searchValue, setSearchValue] = useState('')
    const [open, setOpen] = useState(false)
    const [gData, setGData] = useState([])
    const [value, setValue] = useState(undefined)
    const [save, setSave] = useState(false)
    const [final, setFinal] = useState([])
    useEffect(() => {
        const newestData = makeup(treeData)
        setGData(newestData)
    }, [])

    const handleSelectTree = ((info) => {
        setOpen(false)
        setSave(false)
        setValue({
            value: info.name,
            label: info.name,
            type: info.type,
            allowers: info.allowers
        })
    })

    const helperFindPosition = (p) => {
        return p.split('-').reduce((i, v, idx) => {
            const init = i
            if (idx >= 1) {
                init.push(v)
            }
            return init
        }, [])
    }

    const helperRemoveItem = (p = []) => {
        return p.reduce((init, value, idex) => {
            if (idex === 0) {
                console.log(init[value], '---', value, init)
                return init[value]
            } else {
                return init?.children?.[value]
            }
        }, [gData])
    }

    const handleDel = (p) => {
        console.log('handle delete', )
        // const newData = [...gData]
        const position = helperFindPosition(p)
        const removeItem = helperRemoveItem(position)
        console.log(position ,removeItem)

    }

    const makeup = (d, level = '0') => {
        const result = d.map((node, parentIdx) => {
            const { type, allowers, name, children, title } = node
            let newTitle = title
            let newChildren = children
            if (children) {
                newChildren = makeup(children, `${level}-${parentIdx}`)
            }
            return {
                title: newTitle,
                key: `${level}-${parentIdx}`,
                name,
                allowers,
                type,
                icon: <FolderFilled style={{ fontSize: "20px", paddingTop: "10px" }} />,
                ...children ? {
                    children: newChildren,
                } : {}
            }
        })
        return result
    }

    useEffect(() => {
        if(gData.length) {
            const afterMakeUp = (freshData) => {
                return freshData.map((fd) => {
                    const { title, children, key, allowers, name, type } = fd
                    let newChildren = ''
                    let newTitle = title
                    if (children) {
                        newChildren = afterMakeUp(children)
                    }
                    if (title === 'input') {
                        newTitle = <InputAction keyIndex={key} type={type} allowers={allowers}
                        handleSave={() => {
                            console.log('handle save')
                        }}
                        handleDel={() => handleDel(key)}
                    />
                    }
                    if (title === 'label') {
                        newTitle = <Folder name={name} type={type} handleSelect={() => handleSelectTree()} />
                    }
                        return {
                            ...fd,
                            ...children ? {
                                children: newChildren,
                            } : {},
                            title: newTitle
                        }
                    })
            }
            const finalData = afterMakeUp([...gData])
            setFinal(finalData)
        }
    }, [gData])

    const onDragEnter = info => {
    };

    const onDrop = info => {
        setValue(undefined)
        setSave(false)
        const dropKey = info.node.key;
        const dragKey = info.dragNode.key;
        const dropPos = info.node.pos.split('-');
        const dropPosition = info.dropPosition - Number(dropPos[dropPos.length - 1]);

        const loop = (data, key, callback) => {
            for (let i = 0; i < data.length; i++) {
                if (data[i].key === key) {
                    return callback(data[i], i, data);
                }
                if (data[i].children) {
                    loop(data[i].children, key, callback);
                }
            }
        };
        const data = [...gData];

        let dragObj;
        loop(data, dragKey, (item, index, arr) => {
            arr.splice(index, 1);
            dragObj = item;
        });

        if (!info.dropToGap) {
            loop(data, dropKey, item => {
                item.children = item.children || [];
                item.children.unshift(dragObj);
            });
        } else if (
            (info.node.props.children || []).length > 0 &&
            info.node.props.expanded &&
            dropPosition === 1
        ) {
            loop(data, dropKey, item => {
                item.children = item.children || [];
                item.children.unshift(dragObj);
            });
        } else {
            let ar;
            let i;
            loop(data, dropKey, (item, index, arr) => {
                ar = arr;
                i = index;
            });
            if (dropPosition === -1) {
                ar.splice(i, 0, dragObj);
            } else {
                ar.splice(i + 1, 0, dragObj);
            }
        }
        setGData(makeup(data))
    };

    const handleAddFolder = () => {
        const data = [
            {
                title: "input",
                name: '',
                allowers: [],
                type: 'one',
            },
            ...gData
        ];

        setGData([...makeup(data)])
    }

    return (
        <>
        <div style={{ width: "460px", margin: '30px auto', border: '1px solid #ded7d7', padding: '5px' }}>
            <Select
                labelInValue
                open={open}
                style={{ width: 448 }}
                onClick={(e) => {
                    const { className } = e.target
                    const targetClass = ['ant-select-selection-search-input']
                    if (targetClass.includes(className)) {
                        setOpen(!open)
                    }
                }}
                dropdownStyle={{
                    overflow: 'auto',
                    height: '460px',
                }}
                value={value}
                placeholder="Please select"
                dropdownRender={(menu) => {
                    return <div
                        style={{
                        }}
                    >
                        <div className="g-search-header">
                            <Search
                                placeholder="Search"
                                style={{ width: '269px' }}
                                onChange={(ev) => {
                                }}
                            />
                            <Button
                                onClick={handleAddFolder}
                            >
                                Add New Folder
                            </Button>
                        </div>
                        <Tree
                            className="draggable-tree"
                            defaultExpandAll
                            draggable
                            showIcon
                            blockNode
                            onDragEnter={onDragEnter}
                            onDrop={onDrop}
                            treeData={final}
                        />
                        {/* {menu} */}
                    </div>
                }}
                onChange={(ev) => {
                    console.log(ev)
                }}
            >
                <Option value="lucy">Lucy (101)</Option>
            </Select>
            <div className="g-action-parent">
                <Button
                    onClick={() => {
                        setValue(undefined)
                        setSave(false)
                    }}
                >
                    Cancel
                </Button>
                <Button
                    disabled={!value}
                    onClick={() => setSave(true)}
                >
                    Save
                </Button>
            </div>
        </div>
       {save && <div style={{ width: "460px", margin: '30px auto', border: '1px solid #ded7d7', padding: '5px' }}>
            <h2>Folder info</h2>
        <div>
            <span>{value.label}</span>
            <br></br>
            <span>{value.type === 'all' ? 'Visible to Everyone' : 'Visible to specific users'}</span>
            <br></br>
            <span>{!!value?.allowers?.length && `Visible to ${value.allowers.toString()}`}</span>
        </div>
        </div>}
        </>
    );
}

